@Injectable()
export class AssetRegisterService extends TypeOrmCrudService<AssetRegister> {
  constructor(@InjectRepository(AssetRegister) repo) {
    super(repo);
  }

  public async createDTO(dto: any) {
    return await this.repo.save(dto);
  }

  async deleteByParentId(parentId){
    if(!parentId || parentId <= -1) {
      throw new Error("you must provide an valid user group id in deleteByParentId");
    }

    await this.repo.createQueryBuilder()
    .delete()
    .from(AssetRegister)
    .where("parentId = :id", { id: parentId })
    .execute();
  }

}
