@Injectable()
export class CreditLimitService extends TypeOrmCrudService<CreditLimit> {
  constructor(@InjectRepository(CreditLimit) repo) {
    super(repo);
  }

  async getByAccountId(accountId: number) {
    if(!accountId || accountId === -1) {
      throw new Error('You must supply a valid ID on credit limit service getByID');
    }

    return await this.repo.findOne({ where: { accountId: accountId }});

  }

  async saveCreditLimit(creditLimit: CreditLimit) {
    return await this.repo.save(creditLimit);
  }
}
