
import { Entity, PrimaryGeneratedColumn, Column, CreateDateColumn, UpdateDateColumn, OneToOne, JoinColumn } from "typeorm";
import { Account } from "src/account/account.entity";
import { InvoicePeriod } from "src/invoice-period/invoice-period.entity";
import { InvoiceMethod } from "src/invoice-method/invoice-method.entity";
import { CustomerCategory } from "src/customer-category/customer-category.entity";
