@Entity()
export class CustomerDetails {
  @PrimaryGeneratedColumn() id: number;

  @OneToOne(type => Account) @JoinColumn() account: Account;
  @Column() accountId: number;

  @OneToOne(type => InvoicePeriod) @JoinColumn() invoicePeriod: InvoicePeriod;
  @Column() invoicePeriodId: number;

  @OneToOne(type => InvoiceMethod) @JoinColumn() invoice: InvoiceMethod;
  @Column() invoiceMethodId: number;
  
  @OneToOne(type => CustomerCategory) @JoinColumn() customerCategory: CustomerCategory;
  @Column() customerCategoryId: number;

  @Column() invoiceSplitByPO: boolean = false;
  @Column() invoiceSplitBySite: boolean = false;
  @Column() invoiceSplitByOrderType: boolean = false;

  @Column() licenceNumber: string = '';
  @Column() carrierLicenceNumber: string = '';
  @Column({type: 'date'}) carrierLicenceExpiryDate: string = '';
  @Column() wasteCarrier: string = '';

  @CreateDateColumn() createdAt: string;
  @UpdateDateColumn() updatedAt: string; 

}    
 
